mapper.update:
update recyclelife
set recycleLife_views = recycleLife_views + 1
where recycleLife_num = #{recycleLife_num}

UPDATE recyclelife
SET recycleLife_likes = recycleLife_likes + 1
WHERE recycleLife_num = #{recycleLife_num}

UPDATE recyclelife
SET recycleLife_likes = recycleLife_likes - 1
WHERE recycleLife_num = #{recycleLife_num}
